import React, {useState} from 'react';
import './Header.css';
import navmenu from './navmenu.js';
import {Link} from 'react-router-dom';
import { Switch, Route } from "react-router-dom";
// import ClickAwayListener from 'react-click-away-listener';

function Header() { 
  const [click, setClick] = useState(false);
  const [button, setMenu] = useState(true);

  const menuClick = () => setClick(!click);
  const menuClickAway = () => setClick(false);

  const showMenu = () => {
    if(window.innerWidth <= 992) {
      setMenu(false);
    }
    else {
      setMenu(true);
    }
  }
  window.addEventListener('resize', showMenu);
  // const searchClick = () => setClick(!click);
  return (
    <>
      <header className="main-header">
        <div className="primary-header">
          <div className="navigation container">
            <div className="featured-contact">
              <a href="#">Online Giving</a>
              <a href="#">Contact Us</a>
            </div>
					  {/* End-Featured-Contact */}
					  {/* <!--Logo--> */}
            <div className="logo">
              <a href="#">
                <img className="light-logo" alt="Image Alt Text Here" src="images/gav-church-light.png"/>
                <img className="dark-logo" alt="Image Alt Text Here" src="images/gav-church-dark.png"/>
                {/* <!-- <h1>Gaon Church</h1> --> */}
              </a> 
            </div>
            {/* <!-- End-Logo --> */}
            {/* <!--Mobile Nav Icon(Hamburger Icon)--> */}
            <div className="mob-featured-btn">
              <button className="mob-search-btn">
                <i className="icon-search"></i>
              </button>
              <button className={click ? 'menu-icon':'menu-icon js-active'} onClick={menuClick} onClickAway={menuClickAway}>
                <span className="icon-bar-1"></span>
                <span className="icon-bar-2"></span>
              </button>
            </div>
            {/* <!--End Mobile Nav Icon--> */}
            {/* <!--Mobile-Nav--> */}
            <nav className={click ? 'primary-nav-mobile':'primary-nav-mobile js-active'}>
              {navmenu}
              <div className="mob-featured-nav">
                {/* <!-- Featured-contact --> */}
                <div className="featured-contact">
                  <a href="#">Online Giving</a>
                  <a href="#">Contact Us</a>
                </div>
                {/* <!-- End-Featured-Contact --> */}
                {/* <!--Direction--> */}
                <a className="featured-direction touch-effect" href="#"><i className="icon-location"></i><span>Get Directions</span></a>
                {/* <!--End Direction--> */}
              </div>
            </nav>
            {/* <!--End Mobile-Nav--> */}
            {/* <!--Mobile Search--> */}
            <div className="mob-search">
              <form role="search-form" method="get" className="mob-search-form" action="website url>">
                <i className="icon-search"></i>
                <input id="fullBar" type="search" name="s" placeholder="START TYPING" autocomplete="off" autofocus=""/>
              </form>
              <button className="mob-search-close-btn">
                <i className="icon-close"></i>
              </button>
            </div>
            {/* <!--End Mobile Search--> */}
            {/* <!--Direction--> */}
					  <a className="featured-direction" href="#"><i className="icon-location"></i><span>Get Directions</span></a>
					  {/* <!--End Direction--> */}
          </div>
        </div>	
			  <div className="secondary-header container">
          {/* <!--Desktop-Navigation--> */}
          <nav className="primary-nav-desktop">
            <ul>
              <li><a className="current" href="#">Home</a></li>
              <li><a className="touch-effect" href="#">About</a>
                <ul>
                  <li><a className="touch-effect" href="#">Who we are</a></li>
                  <li><a className="touch-effect" href="#">The Gospel</a></li>
                  <li><a className="touch-effect" href="#">Statement of faith</a></li>
                  <li><a className="touch-effect" href="#">Leadership</a>
                    <ul>
                      <li><a className="touch-effect" href="#">Deacans</a></li>
                      <li><a className="touch-effect" href="#">Elders</a></li>
                    </ul>
                  </li>
                  <li><a className="touch-effect" href="#">Our Ministries</a></li>
                </ul>
              </li>
              <li><a className="touch-effect" href="#">Events</a></li>
              <li><a className="touch-effect" href="#">Blog</a></li>
              <li><a className="touch-effect" href="#">Sermons</a></li>
              <li><a className="touch-effect" href="#">I'm New</a>
                <ul>
                  <li><a className="touch-effect" href="#">What to expect</a></li>
                  <li><a className="touch-effect" href="#">Plan Your Visit</a></li>
                  <li><a className="touch-effect" href="#">Service Times &amp; Direction</a></li>
                </ul>
              </li>
            </ul>
					  {/* <!--Desktop Search--> */}
            <div className="search">
              <form role="desk-search-form" method="get" className="search-form" action="website url>">
                <button>
                  <i className="icon-search"></i>
                </button>
                <input id="fullBar" type="search" name="s" placeholder="START TYPING" autocomplete="off" autofocus=""/>
              </form>
              <button className="search-close-btn">
                <i className="icon-search"></i>
                <span>Search</span>
                <i className="icon-close"></i>
              </button>
            </div>
				  	{/* <!--End Desktop Search--> */}
				  </nav>
			  	{/* <!--End Desktop Navigation--> */}
			  </div>	
		  </header>
    </>
  )
}

export default Header
