import React, {useState, useEffect} from 'react';
import Icon from "./Icon";
import './Socialshare.css';
import $ from 'jquery';


function Socialshare() {
  const [click, setClick] = useState(false);
  const [button, setMenu] = useState(true);

  const shareClick = () => setClick(!click);

  $(".share-btn").on('click', function(){
    $(this).siblings(".social-share").toggleClass("js-active")
  });
  return (
    <>
      <section className="share-posts">
        <button className={click ? 'touch-effect share-btn js-active':'touch-effect share-btn'} onClick={menuClick}>
          <span>Share this article</span>
          <Icon icon="share" />
        </button>
        <nav className="social-share-posts social-share">
          <button className="fb-share touch-effect"><i className="icon-facebook1"></i></button>
          <button onclick="sendMail(); return false" className="email-share touch-effect"><i className="icon-email"></i></button>
          <button className="twitt-share touch-effect"><i className="icon-twitter"></i></button>
          <button className="copy-link touch-effect" data-clipboard-target="#input-url">
            <i className="icon-link"></i>
            <input id="input-url" className="sr-only" value=""/>
          </button>
        </nav>
      </section>
    </>
  )
}

export default Socialshare
